mapper.select:
SELECT
	SUM(deposit_amount)
FROM
	deposit_info
WHERE
	deposit_info.user_id = #{userId}
AND
	deposit_year = #{year}
AND
	deposit_month = #{month}

SELECT
	SUM(spending_amount)
FROM
	spending_info
WHERE
	spending_info.user_id = #{userId}
AND
	spending_year = #{year}
AND
	spending_month = #{month}
